mapper.delete:
delete from rel_role_project where project_id = #{projectId} and role_id in (select id from role where org_id = #{orgId});
delete from rel_role_dashboard_widget where role_id in (select id from role where org_id = #{orgId}) and mem_dashboard_widget_id in ( select id from mem_dashboard_widget where dashboard_id in ( select d.id from dashboard d inner join dashboard_portal dp on dp.id = d.dashboard_portal_id where dp.project_id = #{projectId} ) );
delete from rel_role_dashboard where role_id in (select id from role where org_id = #{orgId}) and dashboard_id in ( select d.id from dashboard d inner join dashboard_portal dp on dp.id = d.dashboard_portal_id where dp.project_id = #{projectId} );
delete from rel_role_portal where role_id in (select id from role where org_id = #{orgId}) and portal_id in (select id from dashboard_portal where project_id = #{projectId});
delete from rel_role_display_slide_widget where role_id in (select id from role where org_id = #{orgId}) and mem_display_slide_widget_id in (select m.id from mem_display_slide_widget m right join display_slide s on s.id = m.display_slide_id right join display d on d.id = s.display_id where d.project_id = #{projectId});
delete from rel_role_slide where role_id in (select id from role where org_id = #{orgId}) and slide_id in (select s.id from display_slide s right join display d on d.id = s.display_id and d.project_id = #{projectId});
delete from rel_role_display where role_id in (select id from role where org_id = #{orgId}) and display_id in (select id from display where project_id = #{projectId});
delete from rel_role_view where role_id in (select id from role where org_id = #{orgId}) and view_id in (select id from view where project_id = #{projectId});
delete from rel_project_admin where project_id = #{projectId};

mapper.sql:
select p.*, if(s.`id` is null, false, true) as 'isStar', u.`id` as 'createBy.id', IF(u.`name` is NULL, u.`username`, u.`name`) as 'createBy.username', u.`avatar` as 'createBy.avatar' from project p left join `user` u on u.`id` = p.`user_id` left join star s on (s.target_id = p.id and s.`target` = 'project' and s.user_id = #{userId}) where p.id in (
select distinct p.id from project p left join rel_project_admin rpa on rpa.project_id = p.id where p.user_id = #{userId} or rpa.user_id = #{userId}
union all
select DISTINCT p.id from project p left join rel_role_project rrp on rrp.project_id = p.id left join rel_role_user rru on rru.role_id = rrp.role_id where rru.user_id = #{userId}
union all
select distinct p.id from project p left join rel_user_organization ruo on ruo.org_id = p.org_id left join organization o on o.id = p.org_id where o.user_id = #{userId}
union all
select distinct p.id from project p left join rel_user_organization ruo on ruo.org_id = p.org_id left join organization o on o.id = p.org_id where ruo.user_id = #{userId} and (ruo.role = 1 or (p.visibility = 1 and o.member_permission = 1)) )

